import axios from 'axios'
import React, { Component } from 'react'

import '../Images'



class ProductList extends Component {

    constructor(props) {
        super(props)

        this.state = {

            Products : []

        }
    }        

     componentDidMount(){
        //   axios.get('/Products')
          fetch('/Products')
         .then(res => res.json())
         .then(res2 => {console.log(res2)
            this.setState({
                Products : res2
            })  
        })



    //     // this.setState({ ProductList: response.data.ProductList})

     }
    renderImage(image1){

        const url = '../Images/' + image1
        console.log(`.... ..... `)
        console.log(url)
        return (
        <img src={require(`${url}`).default} />
        )
    }

    render() {

        const list = this.state.Products.map(item => {
            return <div>

                {this.renderImage(item.image1)}
                <p>{item.name}</p>
                <p>{item.brand}</p>
                <p>Bidding date {item.bidDate}</p>

            </div>
        })

        return (

            <div>
                {list}

            </div>
        )
    }
}

export default ProductList
