import React, { Component } from 'react'


function importAll(r) {
    let images = {};
    r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
    return images;
  }

  var images = importAll(require.context('../Images', false, /\.(png|jpe?g|svg)$/));  

class ProductList extends Component {

    constructor(props) {
        super(props)

        this.state = {

            Products : [],
            u : '../Images/1613989322352_website-homepage-poor-visual-hierarchy.png'

        }
    }        

     componentDidMount(){
        //   axios.get('/Products')
          fetch('/Products')
         .then(res => res.json())
         .then(res2 => {console.log(res2)
            this.setState({
                Products : res2
            })  
        })




    //     // this.setState({ ProductList: response.data.ProductList})

     }







    render() {

        const list = this.state.Products.map(item => {
            return <div>


                <p>{item.name}</p>
                <p>{item.brand}</p>
                <p>Bidding date {item.bidDate}</p>
                <p>{item.image1}</p>
                {/* <img src={require(item.image1).default} alt="abcs" />  */}
                {/* <img src={require(`${item.image1}`).default} alt="abc" /> */}

                <img src={images[item.image1].default} alt="aa"/>
            </div>
        })

        return (

            <div>
                {list}

            </div>
        )
    }
}

export default ProductList
